package dev.dluks.dscommerce.dtos;import dev.dluks.dscommerce.models.Product;import jakarta.validation.constraints.*;import java.util.ArrayList;import java.util.List;public class ProductDTO {    Long id;    @Size(min = 3, max = 80, message = "Nome deve ter entre 3 e 80 caracteres")    @NotBlank(message = "Nome é obrigatório")    String name;    @Size(min = 10, message = "Descrição deve ter no mínimo 10 caracteres")    @NotBlank(message = "Nome é obrigatório")    String description;    @NotNull(message = "Preço é obrigatório")    @Positive(message = "Preço deve ser um valor positivo")    Double price;    String imgUrl;    @NotEmpty(message = "Deve ter pelo menos uma categoria")    List<CategoryDTO> categories = new ArrayList<>();    public ProductDTO(Long id, String name, String description, Double price, String imgUrl) {        this.id = id;        this.name = name;        this.description = description;        this.price = price;        this.imgUrl = imgUrl;    }    public ProductDTO(Product entity) {        id = entity.getId();        name = entity.getName();        description = entity.getDescription();        price = entity.getPrice();        imgUrl = entity.getImgUrl();        entity.getCategories().forEach(category -> categories.add(new CategoryDTO(category)));    }    public Long getId() {        return id;    }    public @Size(min = 3, max = 80, message = "Nome deve ter entre 3 e 80 caracteres") @NotBlank(message = "Nome é obrigatório") String getName() {        return name;    }    public @Size(min = 10, message = "Descrição deve ter no mínimo 10 caracteres") @NotBlank(message = "Nome é obrigatório") String getDescription() {        return description;    }    public @NotNull(message = "Preço é obrigatório") @Positive(message = "Preço deve ser um valor positivo") Double getPrice() {        return price;    }    public String getImgUrl() {        return imgUrl;    }    public List<CategoryDTO> getCategories() {        return categories;    }}