package dev.dluks.dscommerce.dtos;import dev.dluks.dscommerce.models.Product;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Positive;import jakarta.validation.constraints.Size;public record ProductDTO(        Long id,        @Size(min = 3, max = 80, message = "Nome deve ter entre 3 e 80 caracteres")        @NotBlank(message = "Nome é obrigatório")        String name,        @Size(min = 10, message = "Descrição deve ter no mínimo 10 caracteres")        @NotBlank(message = "Nome é obrigatório")        String description,        @NotNull(message = "Preço é obrigatório")        @Positive(message = "Preço deve ser um valor positivo")        Double price,        String imgUrl) {    public ProductDTO(Product product) {        this(product.getId(),                product.getName(),                product.getDescription(),                product.getPrice(),                product.getImgUrl());    }}